pragma solidity 0.8.4;

import '../libraries/DataStruct.sol';
import '../libraries/Errors.sol';
import '../libraries/Math.sol';
import '../libraries/WadRayMath.sol';

library AssetBond {
  using WadRayMath for uint256;
  using AssetBond for DataStruct.AssetBondData;

  event TotalATokenSupplyUpdated(
    address underlyingAsset,
    uint256 id,
    uint256 averageMoneyPoolAPR,
    uint256 totalATokenBalanceOfMoneyPool
  );

  //   uint256 constant COUNTRY_CODE =
  //   uint256 constant CSP_IDENTIFICATION_NUMBER = ;
  //   uint256 constant COLLATERAL_LATITUDE = ;
  //   uint256 constant COLLATERAL_LONGITUDE = ;
  //   uint256 constant COLLATERAL_DETAILS = ;
  //   uint256 constant NONCE = ;
  //   uint256 constant ??? = ;
  //   uint256 constant COLLATERAL_CATEGORY = ;

  // uint256 constant COUNTRY_CODE_START= 0;
  // uint256 constant CSP_IDENTIFICATION_NUMBER_START= 3;
  // uint256 constant COLLATERAL_LATITUDE_START= 18;
  // uint256 constant COLLATERAL_LONGITUDE_START= 30;
  // uint256 constant COLLATERAL_DETAILS_START= 42;
  // uint256 constant NONCE_START= 52;
  // uint256 constant _START= 55;
  // uint256 constant COLLATERAL_CATEGOR_START= 60;
  function settleAssetBond(
    DataStruct.AssetBondData storage assetBondData,
    address asset,
    address borrower,
    address lawfirm,
    uint256 collateralValue,
    uint256 dueDate,
    string memory ipfsHash
  ) internal {
    assetBondData.asset = asset;
    assetBondData.borrower = borrower;
    assetBondData.lawfirm = lawfirm;
    assetBondData.collateralValue = collateralValue;
    assetBondData.dueDate = dueDate;
    assetBondData.ipfsHash = ipfsHash;
    assetBondData.lastUpdateTimestamp = block.timestamp;
    assetBondData.state = DataStruct.AssetBondState.SETTLED;
  }

  function collateralizeAssetBond(
    DataStruct.AssetBondData storage assetBondData,
    uint256 borrowAmount,
    uint256 borrowAPR
  ) internal {
    // update tokenizer data
    //reserve.totalDepositedAssetBondCount += 1;

    // set bond date data
    assetBondData.borrowAPR = borrowAPR;
    assetBondData.state = DataStruct.AssetBondState.COLLATERALIZED;
    assetBondData.lastUpdateTimestamp = block.timestamp;
    assetBondData.issuanceDate = block.timestamp;
    assetBondData.maturityDate = block.timestamp + (assetBondData.dueDate * 1 days);
  }

  function releaseAssetBond(DataStruct.AssetBondData storage assetBondData) internal {
    assetBondData.state = DataStruct.AssetBondState.MATURED;
  }

  function validateSettleABToken(uint256 tokenId, address lawfirm) internal view {
    // checks whether lawfirm authorized
    // if (assetBond.state != AssetBondState.EMPTY) revert(); ////
    // access control : check lawfirm
  }

  function validateTokenId(uint256 id) internal {
    // validate id
    //// error InvalidABTokenID(id)
  }
}

contracts/logic/TokenizerData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import '../libraries/DataStruct.sol';
import '../libraries/Errors.sol';
import '../libraries/Math.sol';
import '../libraries/WadRayMath.sol';

library TokenizerData {
  using WadRayMath for uint256;
  using TokenizerData for DataStruct.TokenizerData;

  event MoneyPoolATokenDataUpdated(
    address underlyingAsset,
    uint256 id,
    uint256 averageATokenAPR,
    uint256 totalATokenBalanceOfMoneyPool
  );

  event TotalATokenUpdated(
    address underlyingAsset,
    uint256 averageATokenAPR,
    uint256 totalATokenSupply
  );

  struct IncreaseATokenBalanceLocalVars {
    uint256 newAverageATokenAPR;
    uint256 newBalance;
  }

  function increaseTotalATokenSupply(
    DataStruct.TokenizerData storage tokenizerData,
    uint256 amountIn,
    uint256 rate
  ) internal {
    IncreaseATokenBalanceLocalVars memory vars;

    (vars.newBalance, vars.newAverageATokenAPR) = Math.calculateRateInIncreasingBalance(
      tokenizerData.averageATokenAPR,
      tokenizerData.totalATokenSupply,
      amountIn,
      rate
    );

    tokenizerData.averageATokenAPR = vars.newAverageATokenAPR;
    tokenizerData.totalATokenSupply = vars.newBalance;
    tokenizerData.lastUpdateTimestamp = block.timestamp;

    emit TotalATokenUpdated(tokenizerData.asset, vars.newAverageATokenAPR, vars.newBalance);
  }

  struct DecreaseATokenBalanceLocalVars {
    uint256 newAverageATokenAPR;
    uint256 newBalance;
  }

  function decreaseTotalATokenSupply(
    DataStruct.TokenizerData storage tokenizerData,
    uint256 amountOut,
    uint256 rate
  ) internal {
    IncreaseATokenBalanceLocalVars memory vars;

    (vars.newBalance, vars.newAverageATokenAPR) = Math.calculateRateInDecreasingBalance(
      tokenizerData.averageATokenAPR,
      tokenizerData.totalATokenSupply,
      amountOut,
      rate
    );

    tokenizerData.averageATokenAPR = vars.newAverageATokenAPR;
    tokenizerData.totalATokenSupply = vars.newBalance;

    emit TotalATokenUpdated(tokenizerData.asset, vars.newAverageATokenAPR, vars.newBalance);
  }

  function validateSettleABToken(uint256 tokenId, address lawfirm) internal view {
    // checks whether lawfirm authorized
    // if (assetBond.state != AssetBondState.EMPTY) revert(); ////
    // access control : check lawfirm
  }

  function validateTokenId(uint256 id) internal {
    // validate id
    //// error InvalidABTokenID(id)
  }
}

contracts/logic/Validation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import '../libraries/DataStruct.sol';
import '../libraries/Errors.sol';
import '../libraries/Math.sol';
import '../interfaces/ILToken.sol';

library Validation {
  using WadRayMath for uint256;
  using Validation for DataStruct.ReserveData;

  /**
   * @dev Validate Invest
   * Check reserve state
   * @param reserve The reserve object
   * @param amount Investment amount
   **/
  function validateInvestMoneyPool(DataStruct.ReserveData storage reserve, uint256 amount)
    internal
    view
  {
    if (amount == 0) revert(); //// InvalidAmount(amount)

    if (reserve.isPaused == true) revert(); //// ReservePaused();

    if (reserve.isActivated == false) revert(); //// ReserveInactivated();
  }

  /**
   * @dev Validate WithdrawMoneyPool
   * Check reserve state
   * Check user amount
   * Check user total debt(later)
   * @param reserve The reserve object
   * @param underlyingAsset Underlying asset address
   * @param amount Withdraw amount
   * @param reserveList reserve list for calculating user total debt
   * @param reserveCount reserve count for iteration
   **/
  function validateWithdrawMoneyPool(
    DataStruct.ReserveData storage reserve,
    address underlyingAsset,
    uint256 amount,
    uint256 userLTokenBalance,
    mapping(uint256 => address) storage reserveList,
    uint256 reserveCount
  ) internal view {
    if (amount == 0) revert(); //// InvalidAmount(amount)
    if (reserve.isPaused == true) revert(); //// ReservePaused();
    if (reserve.isActivated == false) revert(); //// ReserveInactivated();
    if (amount > userLTokenBalance) revert(); //// WithdrawInsufficientBalance(amount, userLTokenBalance);
  }

  /**
   * @dev Validate invest ABToken
   * Check reserve state
   * Check ABToken state
   * Check user amount
   * Check user total debt(later)
   * @param reserve The reserve object
   * @param assetBond The assetBond object
   * @param amount Withdraw amount
   **/
  function validateInvestABToken(
    DataStruct.ReserveData storage reserve,
    DataStruct.AssetBondData memory assetBond,
    uint256 amount,
    uint256 moneyPoolATokenBalance
  ) internal view {
    if (amount == 0) revert(); //// InvalidAmount(amount)
    if (reserve.isPaused == true) revert(); //// ReservePaused();
    if (reserve.isActivated == false) revert(); //// ReserveInactivated();

    //if(assetBond.isMatured == true) revert(); //// MaturedABToken();
    //if(assetBond.isDeposited == false) revert(); //// NotDepositedABToken();
    if (moneyPoolATokenBalance < amount) revert(); //// InsufficientATokenBalance(reserve.totalDepositedATokenBalance);
  }

  function validateBorrowAgainstAssetBond(
    DataStruct.ReserveData storage reserve,
    DataStruct.AssetBondData memory assetBond,
    address asset,
    uint256 borrowAmount
  ) internal view {
    // moneypool validate logic : active, frozen

    // check settled logic
    //if (assetBond.isSettled == true) revert(); //// NotSettledABToken(id);

    // check sign logic
    //if (assetBond.isSigned == false) revert(); ////NotSignedABToken(id);

    uint256 availableLiquidity = IERC20(asset).balanceOf(reserve.lTokenAddress);
  }

  function validateLTokenTrasfer() internal pure {}
}

contracts/interfaces/IMoneyPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import '../libraries/DataStruct.sol';

interface IMoneyPool {
  event InvestMoneyPool(address indexed asset, address indexed account, uint256 amount);

  event WithdrawMoneyPool(
    address indexed asset,
    address indexed account,
    address indexed to,
    uint256 amount
  );

  event BorrowAgainstAssetBond(
    address indexed asset,
    address indexed borrower,
    address indexed receiver,
    uint256 tokenId,
    uint256 borrowAPR,
    uint256 borrowAmount
  );

  event RepayAgainstAssetBond();

  function investMoneyPool(
    address asset,
    address account,
    uint256 amount
  ) external;

  function withdrawMoneyPool(
    address asset,
    address account,
    uint256 amount
  ) external returns (uint256);

  function borrowAgainstABToken(
    address asset,
    address receiver,
    uint256 borrowAmount,
    uint256 tokenID
  ) external;

  function getLTokenInterestIndex(address asset) external view returns (uint256);

  function getReserveData(address asset) external view returns (DataStruct.ReserveData memory);

  function validateLTokenTransfer(
    address asset,
    address from,
    address to,
    uint256 amount,
    uint256 previousFromBalance,
    uint256 previousToBalance
  ) external;

  function addNewReserve(
    address asset,
    address lToken,
    address dToken,
    address interestModel,
    address tokenizer,
    uint256 moneyPoolFactor_
  ) external;
}

